import { BrowserRouter as Router, Routes, Route, Link, Navigate } from "react-router-dom";
import { useLocation } from 'react-router-dom';
import { useState, useRef } from 'react';

function Scenarios() {
    const location = useLocation();

    const [leftWidth, setLeftWidth] = useState(window.innerWidth / 2);
    const dividerRef = useRef(null);

    const startResizing = (e) => {
        e.preventDefault();
        document.addEventListener('mousemove', resize);
        document.addEventListener('mouseup', stopResizing);
    };

    const resize = (e) => {
        const newLeftWidth = e.clientX;
        setLeftWidth(newLeftWidth);
    };

    const stopResizing = () => {
        document.removeEventListener('mousemove', resize);
        document.removeEventListener('mouseup', stopResizing);
    };

    let content;
    if (location.pathname === "/scenarios/make") {
        content =
            <div>
                <Link to="/scenarios/use" className="LinkNoDecor">Use</Link>
            </div>;
    } else if (location.pathname === "/scenarios/use") {
        content =
            <div>
                <Link to="/scenarios/make" className="LinkNoDecor">Make</Link>
            </div>;
    } else {
        content = <div>ERROR</div>;
    }

    useEffect(() => {
        const handleResize = () => {
            // Implement logic to adjust the split view based on the window size
            // For simplicity, you can reset to 50-50 split if window.innerWidth < 800
            if (window.innerWidth < 800) {
                setLeftWidth(window.innerWidth / 2);
            }
        };

        window.addEventListener('resize', handleResize);

        return () => {
            window.removeEventListener('resize', handleResize);
        };
    }, []);


    return (
        <div className="split-container" style={{ display: 'flex', height: '100%' }}>
            <div className="left-panel" style={{ width: `${leftWidth}px`, minWidth: '400px' }}>Left Panel Content</div>
            <div ref={dividerRef} onMouseDown={startResizing} className="divider" style={{ cursor: 'col-resize', width: '5px', backgroundColor: '#000' }}></div>
            <div className="right-panel" style={{ flex: 1, minWidth: '400px' }}>Right Panel Content</div>
        </div>
    );
}

export default Scenarios;
