import React, { createContext, useContext, useState } from 'react';

const CompilerSettings = createContext();

export function useScenario() {
    return useContext(CompilerSettings);
}

export const ScenarioProvider = ({ children }) => {
    const [vul, setVul] = useState('None');
    const [dealer, setDealer] = useState('Random'); // Default to 'Random'
    const [numberOfDeals, setNumberOfDeals] = useState(1); // Default to 1

    const value = {
        vul,
        setVul,
        dealer,
        setDealer,
        numberOfDeals,
        setNumberOfDeals,
    };

    return <ScenarioContext.Provider value={value}>{children}</ScenarioContext.Provider>;
};
