import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';

function Scenarios() {
    const location = useLocation();
    const [leftWidth, setLeftWidth] = useState(window.innerWidth / 2);
    const [isDragging, setIsDragging] = useState(false);

    // Adjust panes based on window size
    useEffect(() => {
        function handleResize() {
            if (window.innerWidth < 800) {
                setLeftWidth(window.innerWidth / 2); // Set to half of window size
            }
        }

        window.addEventListener('resize', handleResize);
        return () => window.removeEventListener('resize', handleResize);
    }, []);

    // Start dragging
    const startDragging = () => {
        setIsDragging(true);
        document.addEventListener('mousemove', onMouseMove);
        document.addEventListener('mouseup', stopDragging);
    };

    // While dragging
    const onMouseMove = (e) => {
        if (!isDragging) return;
        const newWidth = e.clientX;
        const minWidth = 400; // Minimum width of the pane
        const maxWidth = window.innerWidth - minWidth; // Maximum width considering the other pane
        if (newWidth > minWidth && newWidth < maxWidth && window.innerWidth >= 800) {
            setLeftWidth(newWidth);
        }
    };

    // Stop dragging
    const stopDragging = () => {
        setIsDragging(false);
        document.removeEventListener('mousemove', onMouseMove);
        document.removeEventListener('mouseup', stopDragging);
    };

    let content;
    if (location.pathname === "/scenarios/make") {
        content = (
            <div>
                Make Scenarios
                {/* Other content */}
            </div>
        );
    } else if (location.pathname === "/scenarios/use") {
        content = (
            <div>
                Use Scenarios
                {/* Other content */}
            </div>
        );
    } else {
        content = <div>ERROR</div>;
    }

    return (
        <div style={{ display: 'flex', width: '100%' }}>
            <div style={{ width: leftWidth, minWidth: '400px', flexGrow: 0, flexShrink: 0 }}>
                {content}
            </div>
            <div onMouseDown={startDragging} style={{ cursor: 'ew-resize', width: '10px', backgroundColor: '#ccc' }} />
            <div style={{ flexGrow: 1, minWidth: '400px' }}>
                {/* This is the other pane */}
            </div>
        </div>
    );
}

export default Scenarios;
