import { useState, useEffect } from 'react';
import { useLocation, Link } from 'react-router-dom';
import Split from 'react-split';

function Scenarios() {
    const location = useLocation();
    const [sizes, setSizes] = useState([50, 50]);

    useEffect(() => {
        const handleResize = () => {
            if (window.innerWidth < 800) {
                setSizes([50, 50]);
            } else {
                // Optional: Adjust sizes dynamically or maintain the user's adjustment
            }
        };

        window.addEventListener('resize', handleResize);
        return () => window.removeEventListener('resize', handleResize);
    }, []);

    let content;
    if (location.pathname === "/scenarios/make") {
        content = <Link to="/scenarios/use" className="LinkNoDecor">Use</Link>;
    } else if (location.pathname === "/scenarios/use") {
        content = <Link to="/scenarios/make" className="LinkNoDecor">Make</Link>;
    } else {
        content = <div>ERROR</div>;
    }

    return (
        <Split
            sizes={sizes}
            minSize={400}
            gutterSize={10}
            className="split"
            onDragEnd={(sizes) => setSizes(sizes)}
            gutter={(index, direction) => {
                const gutter = document.createElement('div');
                gutter.className = `gutter gutter-${direction}`;
                return gutter;
            }}
        >
            <div>{content}</div>
            <div>Other content here</div>
        </Split>
    );
}

export default Scenarios;
