import React, { createContext, useContext, useState, useEffect } from 'react';
import axios from 'axios';

const AuthContext = createContext();

export const AuthProvider = ({ children }) => {
    const [isAuthenticated, setIsAuthenticated] = useState(false);

    useEffect(() => {
        const validateSession = async () => {
            console.log('here val');
            try {
                const response = await fetch('http://localhost:5015/Account/ValidateSession', {
                    method: 'GET',
                    credentials: 'include',
                });
                if (response.status === 200) {
                    setIsAuthenticated(true);
                } else {
                    setIsAuthenticated(false);
                }
            } catch (error) {
                console.error('Session validation failed:', error);
                setIsAuthenticated(false);
                setError('Network error, unable to connect to the server.');
            }
        };

        validateSession();
    }, []);

    if (error) {
        return <div className="error-page">Error: {error}</div>; // Custom error message or link to a custom error page component
    }

    // Your normal component rendering
    return (
        <div>
            {isAuthenticated ? "Welcome back!" : "You are not logged in."}
        </div>
    );

    const login = () => {
        setIsAuthenticated(true);
    };

    const logout = () => {
        setIsAuthenticated(false);
    };

    return (
        <AuthContext.Provider value={{ isAuthenticated, login, logout }}>
            {children}
        </AuthContext.Provider>
    );
};

export const useAuth = () => useContext(AuthContext);
