import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from './AuthContext';

function LoginPage() {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const [visibleForm, setVisibleForm] = useState('login');
    const navigate = useNavigate();
    const { login } = useAuth();

    const handleViewToggle = () => {
        if (visibleForm === 'login') {
            moveUp();
            setVisibleForm('signup');
        } else {
            moveDown();
            setVisibleForm('login');
        }
        console.log(visibleForm);
    };

    const moveUp = () => {
    };

    const moveDown = () => {
    };


    const handleSubmit = async (event) => {
        event.preventDefault();
        try {
            fetch('http://localhost:5015/Account/Login', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({username, password}),
            }).then(r => {
                if (r.status === 200) {
                    login();
                    navigate('/');
                } else {
                    setError('Login failed. Please try again later.');
                }
            });
        } catch (error) {
            if (error.response) {
                setError(error.response.data.message);
            } else {
                setError('Login failed. Please try again later.');
            }
        }
    };

    return (
        <div className={"LoginPageImageContrainer"}>
            <div className="LoginContainer">
                <form onSubmit={handleSubmit} className="LoginForm">
                    {(visibleForm === 'login') && (<div className={"LogInFormContent"}>
                        <div className={"LoginFormBreak"}></div><div className={"LoginFormBreak"}></div>
                        <div className={"LogginPageTitle"}>Log in</div>
                        <div className={"LoginFormBreak"}></div><div className={"LoginFormBreak"}></div>
                        <div className="form-group">
                            <label htmlFor="username">Username:</label>
                            <input
                                type="text"
                                id="username"
                                className="LoginInput"
                                value={username}
                                onChange={(e) => setUsername(e.target.value)}
                                required
                            />
                        </div>
                        <div className={"LoginFormBreak"}></div>
                        <div className="form-group">
                            <label htmlFor="password">Password:</label>
                            <input
                                type="password"
                                id="password"
                                className="LoginInput"
                                value={password}
                                onChange={(e) => setPassword(e.target.value)}
                                required
                            />
                        </div>
                        <div className={"LoginFormBreak"}></div><div className={"LoginFormBreak"}></div>
                        <button type="submit" className=" AnyButton LoginButton">Log in</button>
                        <div className={"LoginFormBreak"}></div>
                        {error && <span className="ErrorField">{error}</span>}
                    </div>)}

                    <div className={"BigDiv"} 
                         onClick={handleViewToggle}
                         style={{
                             animation: visibleForm === 'login' ? 'slideDown 0.5s ease-out forwards' : 'slideUp 0.5s ease-out forwards'
                         }}>
                    </div>
                    <div className={"OrDiv"} onClick={handleViewToggle}>or</div>
                    <div className={"SignUpDiv"} onClick={handleViewToggle}>Sign up</div>
                    {(visibleForm === 'signup') && 
                    (<div className={"SignUpFormContent"}>
                        costam costam
                    </div>)}

                </form>
            </div>
        </div>
    );
}

export default LoginPage;
