import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from './AuthContext';

function LoginPage() {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const [visibleForm, setVisibleForm] = useState('login');
    const [showForm, setShowForm] = useState('login');
    const navigate = useNavigate();
    const { login } = useAuth();
    const animationTime = 0.3;

    const handleViewToggle = async () => {
        if (visibleForm === 'login') {
            setShowForm('signup');
            await moveUp();
            setVisibleForm('signup');
        } else {
            setShowForm('login');
            setVisibleForm('login');
            await moveDown();
        }
        console.log(visibleForm);
    };

    const delay = ms => new Promise(res => setTimeout(res, ms));

    const moveUp = async () => {
        await delay(animationTime * 1000);
    };

    const moveDown = async () => {
        await delay(animationTime * 1000);
    };


    const handleSubmit = async (event) => {
        event.preventDefault();
        try {
            fetch('http://localhost:5015/Account/Login', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({username, password}),
            }).then(r => {
                if (r.status === 200) {
                    login();
                    navigate('/');
                } else {
                    setError('Login failed. Please try again later.');
                }
            });
        } catch (error) {
            if (error.response) {
                setError(error.response.data.message);
            } else {
                setError('Login failed. Please try again later.');
            }
        }
    };

    return (
        <div className={"LoginPageImageContrainer"}>
            <div className="LoginContainer">
                <form onSubmit={handleSubmit} className="LoginForm">
                    <div 
                        className={"OrLogInDiv"}
                        onClick={handleViewToggle}
                        style={{ display: `${visibleForm === 'login' ? 'none' : 'flex'}` }}
                    >
                        <span className={"OrSpan"}>or</span>
                        <span className={"LogInSpan"}>Log in</span>
                    </div>
                    {(visibleForm === 'login') && (
                    <div className={"LogInFormContent"}>
                        <div className={"LoginFormBreak"}></div><div className={"LoginFormBreak"}></div>
                        <div className={"LogSignPageTitle"}>Log in</div>
                        <div className={"LoginFormBreak"}></div><div className={"LoginFormBreak"}></div>
                        <div className="form-group">
                            <label htmlFor="username">Username:</label>
                            <input
                                type="text"
                                id="username"
                                className="LoginInput"
                                value={username}
                                onChange={(e) => setUsername(e.target.value)}
                                required
                            />
                        </div>
                        <div className={"LoginFormBreak"}></div>
                        <div className="form-group">
                            <label htmlFor="password">Password:</label>
                            <input
                                type="password"
                                id="password"
                                className="LoginInput"
                                value={password}
                                onChange={(e) => setPassword(e.target.value)}
                                required
                            />
                        </div>
                        <div className={"LoginFormBreak"}></div><div className={"LoginFormBreak"}></div>
                        <button type="submit" className=" AnyButton LogSignButton LoginButton">Log in</button>
                        <div className={"LoginFormBreak"}></div>
                        {error && <span className="ErrorField">{error}</span>}
                    </div>)}

                    <div className={"BigDiv"} 
                         onClick={handleViewToggle}
                         style={{
                             animation: `${showForm === 'login' ? 'slideDown' : 'slideUp'} ${animationTime}s ease-out forwards`
                         }}>
                    </div>
                    <div 
                        className={"OrDiv"}
                        style={{
                            display: `${visibleForm === 'login' && showForm === 'login' ? 'flex' : 'none'}`
                        }}
                        onClick={handleViewToggle}>or</div>
                    <div className={"SignUpDiv"}
                         style={{
                             display: `${visibleForm === 'login' && showForm === 'login' ? 'flex' : 'none'}`
                         }}     
                         onClick={handleViewToggle}>Sign up</div>
                    {(visibleForm === 'signup' || showForm === 'signup') &&
                    (<div 
                        className={"SignUpFormContent"}
                        style={{
                            animation: `${showForm === 'login' ? 'slideDown' : 'slideUp'} ${animationTime}s ease-out forwards`
                        }}>
                        <div className={"LoginFormBreak"}></div><div className={"LoginFormBreak"}></div><div className={"LoginFormBreak"}></div>
                        <div className={"LogSignPageTitle"}>Sign up</div>
                        <div className={"LoginFormBreak"}></div><div className={"LoginFormBreak"}></div>
                            <div className="form-group">
                                <label htmlFor="username">Username:</label>
                                <input
                                    type="text"
                                    id="username"
                                    className="LoginInput"
                                    value={username}
                                    onChange={(e) => setUsername(e.target.value)}
                                    required
                                />
                            </div>
                            <div className={"LoginFormBreak"}></div>
                            <div className="form-group">
                                <label htmlFor="password">Password:</label>
                                <input
                                    type="password"
                                    id="password"
                                    className="LoginInput"
                                    value={password}
                                    onChange={(e) => setPassword(e.target.value)}
                                    required
                                />
                            </div>
                            <div className={"LoginFormBreak"}></div>
                            <div className="form-group">
                                <label htmlFor="password">Password:</label>
                                <input
                                    type="password"
                                    id="password"
                                    className="LoginInput"
                                    value={password}
                                    onChange={(e) => setPassword(e.target.value)}
                                    required
                                />
                            </div>
                            <div className={"LoginFormBreak"}></div><div className={"LoginFormBreak"}></div>
                            <button type="submit" className=" AnyButton LogSignButton SignUpButton">Sign up</button>
                            <div className={"LoginFormBreak"}></div>
                            {error && <span className="ErrorField">{error}</span>}
                    </div>)}
                </form>
            </div>
        </div>
    );
}

export default LoginPage;
