import React, { useState, useEffect } from 'react';

function GenerateExample() {
    const [example, setExample] = useState(null);
    const [isLoading, setIsLoading] = useState(false);

    useEffect(() => {
        // Check local storage for an existing example on component mount
        const storedExample = localStorage.getItem('generatedExample');
        if (storedExample) {
            setExample(JSON.parse(storedExample));
        }
    }, []);

    const generateExample = async () => {
        setIsLoading(true);
        try {
            // Replace this URL with the actual endpoint where your C# method is hosted
            const response = await fetch('http://localhost:5015/Scenarios/GenerateDeals', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                // Include the necessary request body according to your backend needs
                body: JSON.stringify({ /* compilerSettings object structure here */ }),
            });

            if (!response.ok) {
                throw new Error('Network response was not ok');
            }

            const data = await response.json();
            setExample(data);
            localStorage.setItem('generatedExample', JSON.stringify(data)); // Store the example in local storage
        } catch (error) {
            console.error('Failed to fetch:', error);
        } finally {
            setIsLoading(false);
        }
    };

    return (
        <div>
            <button onClick={generateExample} disabled={isLoading}>
                {isLoading ? 'Generating...' : 'Generate'}
            </button>
            {example ? (
                <pre>{example.scriptRawOutput}</pre> // Display the raw script output
            ) : (
                <p>Press 'Generate' to generate</p>
            )}
        </div>
    );
}

export default GenerateExample;
