import React, { useState, useEffect } from 'react';
import { useLocation, Link } from 'react-router-dom';

function Scenarios() {
    const location = useLocation();
    const [dividerPosition, setDividerPosition] = useState(window.innerWidth / 2);
    const [isDragging, setIsDragging] = useState(false);

    useEffect(() => {
        function handleResize() {
            if (window.innerWidth < 800) {
                setDividerPosition(window.innerWidth / 2);
            }
        }
        window.addEventListener('resize', handleResize);

        return () => {
            window.removeEventListener('resize', handleResize);
        };
    }, []);

    useEffect(() => {
        function handleMouseMove(e) {
            if (isDragging) {
                let newPosition = e.clientX;
                const minPosition = 400;
                const maxPosition = window.innerWidth - 400;
                if (newPosition < minPosition) newPosition = minPosition;
                if (newPosition > maxPosition) newPosition = maxPosition;
                setDividerPosition(newPosition);
            }
        }

        function handleMouseUp() {
            setIsDragging(false);
        }

        if (isDragging) {
            window.addEventListener('mousemove', handleMouseMove);
            window.addEventListener('mouseup', handleMouseUp);
        }

        return () => {
            window.removeEventListener('mousemove', handleMouseMove);
            window.removeEventListener('mouseup', handleMouseUp);
        };
    }, [isDragging]);

    const startDragging = () => {
        if (window.innerWidth >= 800) {
            setIsDragging(true);
        }
    };

    let content;
    if (location.pathname === "/scenarios/make") {
        content = <Link to="/scenarios/use" className="LinkNoDecor">Use</Link>;
    } else if (location.pathname === "/scenarios/use") {
        content = <Link to="/scenarios/make" className="LinkNoDecor">Make</Link>;
    }

    return (
        <div style={{ display: 'flex', height: '100%' }}>
            <div style={{ width: dividerPosition, minWidth: '400px', flexGrow: 0, flexShrink: 0, overflow: 'auto' }}>
                {content}
            </div>
            <div onMouseDown={startDragging} style={{ cursor: 'col-resize', width: '10px', background: '#ccc' }} />
            <div style={{ flexGrow: 1, minWidth: '400px', overflow: 'auto' }}>
                Other Content
            </div>
        </div>
    );
}

export default Scenarios;
