import { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import { BrowserRouter as Router, Routes, Route, Link, Navigate } from "react-router-dom";

function Scenarios() {
    const location = useLocation();

    // Determine the content based on the path
    let content;
    if (location.pathname === "/scenarios/make") {
        content = <Link to="/scenarios/use">Use</Link>;
    } else if (location.pathname === "/scenarios/use") {
        content = <Link to="/scenarios/make">Make</Link>;
    }

    // State for managing the divider's position
    const [dividerPosition, setDividerPosition] = useState(window.innerWidth / 2);
    const minPanelSize = 400;

    useEffect(() => {
        function handleResize() {
            setDividerPosition(Math.max(minPanelSize, Math.min(window.innerWidth - minPanelSize, dividerPosition)));
        }

        window.addEventListener('resize', handleResize);
        return () => window.removeEventListener('resize', handleResize);
    }, [dividerPosition]);

    const startResizing = (mouseDownEvent) => {
        mouseDownEvent.preventDefault();

        const handleMouseMove = (mouseMoveEvent) => {
            setDividerPosition(Math.max(minPanelSize, Math.min(window.innerWidth - minPanelSize, mouseMoveEvent.clientX)));
        };

        const handleMouseUp = () => {
            document.removeEventListener('mousemove', handleMouseMove);
            document.removeEventListener('mouseup', handleMouseUp);
        };

        document.addEventListener('mousemove', handleMouseMove);
        document.addEventListener('mouseup', handleMouseUp);
    };

    return (
        <div style={{ display: 'flex', flexDirection: 'row', height: '100%' }}>
            <div style={{ width: dividerPosition, minWidth: '400px', flexGrow: 1 }}>{content}</div>
            <div onMouseDown={startResizing} style={{ cursor: 'col-resize', width: '10px', backgroundColor: 'gray' }} />
            <div style={{ flexGrow: 1, minWidth: '400px' }}>Other Content</div>
        </div>
    );
}

export default Scenarios;
