import React, { useState, useEffect } from 'react';
import { useScenario } from './CompilerSettings';

function GenerateDealSet() {
    const [dealSet, setDealSet] = useState(null);
    const [isLoading, setIsLoading] = useState(false);
    const { vul, dealer, numberOfDeals } = useScenario();

    useEffect(() => {
        const checkForDealSet = () => {
            const storedDealSet = sessionStorage.getItem('generatedDealSet');
            if (storedDealSet) {
                setDealSet(JSON.parse(storedDealSet));
            }
        };

        checkForDealSet();

        // Event listener to clear sessionStorage when the page is fully reloaded
        const handleBeforeUnload = (e) => {
            sessionStorage.removeItem('generatedDealSet'); // Data is cleared on full page reload
        };

        window.addEventListener('beforeunload', handleBeforeUnload);

        return () => {
            window.removeEventListener('beforeunload', handleBeforeUnload);
        };
    }, []);

    useEffect(() => {
        // Sync the currentDealNo with sessionStorage whenever it changes
        sessionStorage.setItem('currentDealNo', currentDealNo.toString());
    }, [currentDealNo]);

    const generateDealSet = async () => {
        setIsLoading(true);
        console.log('Generating deal set...' + vul + '...');
        try {
            const response = await fetch('http://localhost:5015/Scenarios/GenerateDeals', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    vul: vul,
                    dealer: dealer,
                    numberOfDeals: numberOfDeals,
                }),
            });

            if (!response.ok) {
                throw new Error('Network response was not ok');
            }

            const data = await response.json();
            setDealSet(data);
            sessionStorage.setItem('generatedDealSet', JSON.stringify(data)); // Temporarily store the dealSet
            sessionStorage.setItem('currentDealNo', '0');
            sessionStorage.setItem('NumOfDeals', numberOfDeals);
        } catch (error) {
            console.error('Failed to fetch:', error);
        } finally {
            setIsLoading(false);
        }
    };

    const nextDeal = () => {
        console.log('nextDeal');
        let currentDealNo = sessionStorage.getItem('currentDealNo');
        currentDealNo++;
        sessionStorage.setItem('currentDealNo', currentDealNo);
    }

    const previousDeal = () => {
        console.log('previousDeal');
        let currentDealNo = sessionStorage.getItem('currentDealNo');
        currentDealNo--;
        sessionStorage.setItem('currentDealNo', currentDealNo);
    }

    return (
        <div>
            <button onClick={generateDealSet} disabled={isLoading}>
                {isLoading ? 'Generating...' : 'Generate'}
            </button>
            <div>
                {dealSet ? (
                    <div>
                        <button onClick={() => setDealSet(null)}>Clear</button>
                        <button>SaveDealSet</button>
                        <button>Regenerate</button>
                        {sessionStorage.getItem('currentDealNo') > 1 
                            ? <button onClick={previousDeal}>Previous</button> 
                            : null}
                        {sessionStorage.getItem('currentDealNo') < sessionStorage.getItem('NumOfDeals')
                            ? <button onClick={nextDeal}>Next</button> 
                            : <button>AddDeal</button>}
                        <button>RemoveDeal</button>
                        Deal num: {sessionStorage.getItem('currentDealNo')}
                        <pre style={{ overflow: 'auto', height: '400px' }}>{dealSet.scriptRawOutput}</pre>
                    </div>
                ) : (
                    <p>Press 'Generate' to generate</p>
                )}
            </div>
        </div>
    );
}

export default GenerateDealSet;
