import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import '../../css/Scenarios.css'; // Ensure you create this CSS file

function Scenarios() {
  const location = useLocation();
  const [isDragging, setIsDragging] = useState(false);
  const [leftWidth, setLeftWidth] = useState(window.innerWidth / 2);
  const [startX, setStartX] = useState(0);

  const handleMouseDown = (e) => {
    setIsDragging(true);
    setStartX(e.clientX);
  };

  const handleMouseMove = (e) => {
    if (!isDragging) return;

    const dx = e.clientX - startX;
    const newLeftWidth = Math.max(400, Math.min(window.innerWidth - 400, leftWidth + dx));
    setLeftWidth(newLeftWidth);
    setStartX(e.clientX);
  };

  const handleMouseUp = () => {
    setIsDragging(false);
  };

  useEffect(() => {
    const handleResize = () => {
      if (window.innerWidth < 800) {
        setLeftWidth(window.innerWidth / 2);
      }
    };

    window.addEventListener('resize', handleResize);
    return () => window.removeEventListener('resize', handleResize);
  }, []);

  useEffect(() => {
    if (isDragging) {
      window.addEventListener('mousemove', handleMouseMove);
      window.addEventListener('mouseup', handleMouseUp);
    }

    return () => {
      window.removeEventListener('mousemove', handleMouseMove);
      window.removeEventListener('mouseup', handleMouseUp);
    };
  }, [isDragging, startX, leftWidth]);

  let content;
  // Define content based on location

  return (
    <div className="split-container">
      <div className="left-pane" style={{ width: `${leftWidth}px` }}>
        {content}
      </div>
      <div className="divider" onMouseDown={handleMouseDown}></div>
      <div className="right-pane" style={{ width: `calc(100% - ${leftWidth}px)` }}>
        {/* Your right pane content */}
      </div>
    </div>
  );
}

export default Scenarios;
