import React, { useState, useEffect } from 'react';
import axios from 'axios';

function SavedItemsPage() {
    const [scenarios, setScenarios] = useState([]);
    const [dealSets, setDealSets] = useState([]);
    const [activeTab, setActiveTab] = useState('scenarios');

    useEffect(() => {
        const fetchSavedContent = async () => {
            const response = await fetch('http://localhost:5015/SavedContent/GetSavedContent', {
                method: 'GET',
                headers: {
                    'Content-Type': 'application/json',
                    // 'Authorization': `Bearer ${localStorage.getItem('token')}`,
                },
                credentials: 'include',
            });
        };

        fetchSavedContent();
    }, []);

    const handleTabClick = (tabName) => {
        setActiveTab(tabName);
    };

    return (
        <div className="profile-view-container">
            <div className="left-border"></div>
            <div className="profile-view">
                <div className="saved-content-outer-container">
                    <div className="profile-saved-tabs">
                        <button onClick={() => handleTabClick('scenarios')}
                                className={`saved-tab-button ${activeTab === 'scenarios' ? 'active' : ''}`}>
                            CONSTRAINTS
                        </button>
                        <button onClick={() => handleTabClick('dealSets')}
                                className={`saved-tab-button ${activeTab === 'dealSets' ? 'active' : ''}`}>
                            DEAL SETS
                        </button>
                    </div>
                    <div className="saved-content-container">
                        {activeTab === 'scenarios' && (
                            <div className="saved-dynamic saved-constraints-dynamic">
                                {scenarios.length > 0 ? (
                                    <div className="scrollable-list">
                                        {scenarios.map(scenario => (
                                            <div key={scenario.id} className="saved-item saved-constraint">
                                                <button className="saved-item-button">
                                                    {scenario.name}
                                                </button>
                                                {/* Implement deletion similarly to your ASP.NET MVC setup */}
                                            </div>
                                        ))}
                                    </div>
                                ) : <p>No saved constraints found.</p>}
                            </div>
                        )}
                        {activeTab === 'dealSets' && (
                            <div className="saved-dynamic saved-dealsets-dynamic">
                                {dealSets.length > 0 ? (
                                    <div className="scrollable-list">
                                        {dealSets.map(dealSet => (
                                            <div key={dealSet.id} className="saved-item saved-dealset">
                                                <button className="saved-item-button">
                                                    {dealSet.name}
                                                </button>
                                                {/* Implement deletion similarly to your ASP.NET MVC setup */}
                                            </div>
                                        ))}
                                    </div>
                                ) : <p>No saved deal sets found.</p>}
                            </div>
                        )}
                    </div>
                </div>
            </div>
            <div className="right-border"></div>
        </div>
    );
  }

export default SavedItemsPage;
