import React from 'react';
import { useScenario } from './CompilerSettings';
import { useNavigate } from 'react-router-dom';

function UseScenariosChoose() {
    const { setVul } = useScenario();
    const navigate = useNavigate();

    return (
        <div>
            USE SCENARIOS
            <div>
                <select onChange={(e) => setVul(e.target.value)} defaultValue="None">
                    <option value="Random">Random</option>
                    <option value="None">None</option>
                    <option value="All">All</option>
                    <option value="NS">NS</option>
                    <option value="EW">EW</option>
                </select>
                <select onChange={(e) => setDealer(e.target.value)} defaultValue="Random">
                    <option value="N">North</option>
                    <option value="S">South</option>
                    <option value="W">West</option>
                    <option value="E">East</option>
                    <option value="Random">Random</option>
                    <option value="Matching numbers">Matching numbers</option>
                </select>
                <input type="number" defaultValue={1} min={1} onChange={(e) => setNumberOfDeals(parseInt(e.target.value, 10) || 1)} />
            </div>
            <div>
                <button onClick={() => navigate('/scenarios/make')}>Go to Make Scenarios</button>
            </div>
        </div>
    );
}

export default UseScenariosChoose;
