import { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import { Link } from 'react-router-dom';
import Split from 'react-split';

function Scenarios() {
    const location = useLocation();
    const [sizes, setSizes] = useState([50, 50]); // Default sizes in percentages

    // Function to handle window resize
    const handleResize = () => {
        if (window.innerWidth < 800) {
            setSizes([50, 50]); // Equal sizes if window width is less than 800px
        }
    };

    // Effect hook for window resize event listener
    useEffect(() => {
        window.addEventListener('resize', handleResize);

        // Cleanup to remove the event listener
        return () => window.removeEventListener('resize', handleResize);
    }, []);

    let content;
    if (location.pathname === "/scenarios/make") {
        content = <Link to="/scenarios/use" className="LinkNoDecor">Use</Link>;
    } else if (location.pathname === "/scenarios/use") {
        content = <Link to="/scenarios/make" className="LinkNoDecor">Make</Link>;
    } else {
        content = <div>ERROR</div>;
    }

    return (
        <Split
            sizes={sizes}
            minSize={400}
            expandToMin={false}
            gutterSize={10}
            className="split"
            onDragEnd={(sizes) => setSizes(sizes)}
            style={{ height: `calc(100vh - 60px)`, display: 'flex' }} // Adjust the height as necessary
        >
            <div>{content}</div>
            <div>Other content here</div>
        </Split>
    );
}

export default Scenarios;
