import React, { useEffect, useState } from 'react';

function ProfilePage() {
    const [profileData, setProfileData] = useState({ username: '', dealSetsCount: 0, scenariosCount: 0 });
    const [isLoading, setIsLoading] = useState(true);

    useEffect(() => {
        const fetchProfileData = async () => {
            const response = await fetch('/Account/Profile', {
                method: 'GET',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem('token')}`,  // Adjust based on your auth method
                }
            });

            if (response.ok) {
                const data = await response.json();
                setProfileData({
                    username: data.username,
                    dealSetsCount: data.DealSetsCount,
                    scenariosCount: data.ScenariosCount
                });
            } else {
                // Handle errors here, such as redirecting to login or displaying a message
                console.error('Failed to fetch profile data');
            }
            setIsLoading(false);
        };

        fetchProfileData();
    }, []);

    if (isLoading) {
        return <div>Loading...</div>;
    }

    return (
        <div>
            <h1>My Profile</h1>
            <p>Username: {profileData.username}</p>
            <p>Number of Deal Sets: {profileData.dealSetsCount}</p>
            <p>Number of Scenarios: {profileData.scenariosCount}</p>
        </div>
    );
}

export default ProfilePage;
